import React from 'react';

const Option= (props) => (
    <div className='option'>
        <p>{props.count}</p>
        {props.optionText}
        <button
        className="button button--link"
            onClick={(e) => {
                props.handleDeleteOption(props.optionText);
            }}
        >
            Remove
        </button>
    </div>
)

export default Option
