import React from 'react';

class Visible extends React.Component {
    constructor(props) {
        console.log("propppp")
        console.log(props)
        super(props);
        this.switchVisibility = this.switchVisibility.bind(this);
        this.state = {
            visible: true
        }
    }
    switchVisibility() {
        this.setState((prevState) => {
            return {
                visible: !prevState.visible
            }
        })
    }

    render() {
        return (
            <div>
                <div>
                    <button onClick={this.switchVisibility}>{this.state.visible ? "Hide details" : "Show details"}</button>
                </div>
                {this.state.visible && <p>I have this to show</p>}

            </div>
        )
    }
}

export default Visible;

